jQuery(document).ready(function() {

	jQuery(".search-top-barnbt #btn-searchnbt").hide();

	jQuery(".toggle-searchnbt").click(function() {
		jQuery(".wrapper-search-top-bar").toggle();
		jQuery(".wrapper-search-top-bar .txt-searchnbt").focus();
	});

	jQuery(".boton-menu-movil").click(function() {
		jQuery("#menu-movil").toggle();
	});

	jQuery(".boton-menu-movil-sin-top-bar").click(function() {
		jQuery("#menu-movil").toggle();
	});

	jQuery(".ir-arriba").click(function() {
		jQuery('html, body').animate({ scrollTop: 0 }, 'fast');
	});

	// Show / Hide 'Back to top' button
	if (jQuery('.ir-arriba').length) { // Comprobamos que exista el div (se ha podido desactivar en las opciones)
		var refScroll = jQuery('#mainnbt');
		var refScroll_offset = refScroll.offset();

		jQuery(window).on('scroll', function() {
			if(jQuery(window).scrollTop() > refScroll_offset.top) {
			  jQuery(".ir-arriba").show(500);
			} else {
			  jQuery(".ir-arriba").hide();
			}
		});
	}

	// Collapse and expand menus on mobile devices
	jQuery('.menu-movil-enlaces li.menu-item-has-children').addClass('submenu-toggle');
	jQuery('.menu-movil-enlaces li.menu-item-has-children').addClass('submenu-colapsado');


    jQuery("li.submenu-toggle").click(function(e) {
		if (jQuery(this).hasClass('submenu-colapsado')){

			jQuery(this).toggleClass('submenu-expandido');
			jQuery(this).find('ul').first().toggle();

		}else{

			jQuery(this).toggleClass('submenu-colapsado');
			jQuery(this).find('ul').first().toggle();

		}
		e.stopPropagation();
});



	jQuery(".widget-areanbt .widget > h2").wrapInner("<span class='widget-title-tab'/>");

});

